const button=document.querySelector(".button-form-open");const forma=document.querySelector(".form");const in_date=forma.querySelector("[id=check-in-date]");const out_date=forma.querySelector("[id=check-out-date]");const form=forma.querySelector("form");button.addEventListener("click",function(a){a.preventDefault();forma.classList.toggle("active-form")});form.addEventListener("submit",function(a){if(!in_date.value||!out_date.value){a.preventDefault();forma.classList.add("modal-error");setTimeout(function(){forma.classList.remove("modal-error")},600);console.log("Нужно ввести даты")}});